mapper.select:

	select  *
    	from(
            select rownum rn , a.*
            from
            	(
            	select
           		p.postsn , p.title , p.createdatetime , u.usersn , u.nick , b.boardcomment , p.replycnt
            	from boardpost p ,  userdata u , board b
    			where u.usersn = p.usersn 
    			and b.boardsn = p.boardsn
    			order by p.postsn desc
    			)a



		where rownum <= #{cri.pageNum} * #{cri.amount}
			)
	where rn > (#{cri.pageNum} -1 ) * #{cri.amount}


	select *
	from (
		select rownum list , a.*
		from
			(
			select row_number() over(order by p.postsn , rownum desc) rn ,
			b.boardsn , p.postsn , p.title, p.createdatetime , b.boardComment, u.nick , p.replycnt

			from board b , boardpost p , userdata u
			where b.boardsn = #{boardSN}
			and b.boardsn = p.boardsn
			and u.usersn = p.usersn
			order by p.postsn desc
			) a
			where


  	rownum <= #{cri.pageNum} * #{cri.amount}
  	)
  	where list > (#{cri.pageNum} -1 ) * #{cri.amount}

mapper.update:
update boardpostcontent
set postContent = #{postContent}
where postSN = #{postSN}

update boardpost 
set replycnt = replycnt + #{amount} 
where postSN = #{postSN}
