mapper.select:

	select  *
    	from(
            select rownum rn , a.*
            from
            	(
            	select
           		p.postsn , p.title , p.createdatetime , u.usersn , u.nick , c.postcontent
            	from boardpost p , boardpostcontent c , userdata u
    			where u.usersn = p.usersn and p.postsn = c.postsn
    			order by postsn desc
    			)a
			where 





			 rownum <= #{pageNum} * #{amount} 
			)
		where rn > (#{pageNum} -1 ) * #{amount}

mapper.update:
update boardpostcontent
set postContent = #{postContent}
where postSN = #{postSN}
