mapper.select:

select replySN , postSN, userSN , createdatetime ,replyContent
from(
		select /* + INDEX(boardpostreply idx_reply) */
		rownum rn, replySN, postSN, userSN, createdatetime ,replyContent
		from boardpostreply
		where postSN = #{postSN}
		and replySN > 0
		and rownum <= #{cri.pageNum} * #{cri.amount}
	)
where rn > (#{cri.pageNum} -1 ) * #{cri.amount}
