mapper.select:
SELECT * FROM uniz
WHERE upper(unizKeyword) IN

		#{keyword}

	AND unizTypeSN IN

		#{unizTypeSN}


SELECT u.*
FROM myunizpointlist m, uniz u
WHERE m.unizSN = u.unizSN
	AND m.userSN = #{userSN}
	AND m.point >= #{fvPoint}
	AND ROWNUM <= #{limit}
ORDER BY m.point DESC

SELECT PARENTUNIZSN
FROM UNIZLAYERLIST
WHERE UNIZSN = #{utbCateSN}
